func (api *ServerAPI) request(f func(*sacloud.Response) error) (*sacloud.Server, error) {
	res := &sacloud.Response{}
	err := f(res)
	if err != nil {
		return nil, err
	}
	return res.Server, nil
}

func (api *ServerAPI) createRequest(value *sacloud.Server) *sacloud.Request {
	req := &sacloud.Request{}
	req.Server = value
	return req
}
